// // src/integrations/HubspotIntegration.js

import { Button, Typography, List, ListItem, ListItemText, CircularProgress, Box } from '@mui/material';
import { useState } from 'react';
import { authorize, getCredentials, loadItems } from './hubspot';

export const HubspotIntegration = ({ user, org, setIntegrationParams }) => {
  const [loadedData, setLoadedData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleConnect = async () => {
    try {
      await authorize(user, org);
    } catch (err) {
      console.error(err);
      setError("Authorization failed.");
    }
  };

  const handleCredentials = async () => {
    try {
      const creds = await getCredentials(user, org);
      setIntegrationParams({
        type: 'hubspot',
        credentials: creds,
      });
      alert("Credentials retrieved. Now click 'Load Data'.");
    } catch (err) {
      console.error(err);
      setError("Failed to get credentials.");
    }
  };

  const handleLoadData = async () => {
    setError('');
    setLoading(true);
    try {
      const creds = await getCredentials(user, org);
      const data = await loadItems(creds);
      setLoadedData(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load data from HubSpot.");
    } finally {
      setLoading(false);
    }
  };

  const handleClearData = () => {
    setLoadedData([]);
    setError('');
  };

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        HubSpot Integration
      </Typography>

      <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
        <Button variant="contained" onClick={handleConnect}>
          Authorize HubSpot
        </Button>
        <Button variant="outlined" onClick={handleCredentials}>
          Get Credentials
        </Button>
      </Box>

      <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
        <Button variant="contained" color="primary" onClick={handleLoadData}>
          Load Data
        </Button>
        <Button variant="outlined" color="secondary" onClick={handleClearData}>
          Clear Data
        </Button>
      </Box>

      {loading && <CircularProgress sx={{ my: 2 }} />}

      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}

      {loadedData.length > 0 ? (
        <>
          <Typography variant="subtitle1">Loaded Contacts:</Typography>
          <List dense>
            {loadedData.map((item) => (
              <ListItem key={item.id} divider>
                <ListItemText
                  primary={item.name}
                  secondary={
                    item.url ? (
                      <a href={item.url} target="_blank" rel="noreferrer">
                        {item.url}
                      </a>
                    ) : null
                  }
                />
              </ListItem>
            ))}
          </List>
        </>
      ) : (
        <Typography variant="body2" color="textSecondary">
          No data loaded.
        </Typography>
      )}
    </Box>
  );
};
